import { Add, Remove } from "@mui/icons-material";
import React from "react";
import styled from "styled-components";
import Fotter from "../components/Fotter";
import Navbar from "../components/Navbar";
import { Notification } from "../components/Notification";

const Container = styled.div``;
const Wrapper = styled.div`
  padding: 20px;
`;
const Title = styled.h1`
  text-align: center;
`;
const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`;
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  text-transform: uppercase;
  border: ${(props) => props.type === "filled" && "none"};
  background-color: ${(props) =>
    props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" && "white"};
`;

const TopTexts = styled.span``;

const TopText = styled.span`
  cursor: pointer;
  font-weight: 600;
  margin: 0px 10px;
  text-decoration: underline;
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Info = styled.div`
  flex: 3;
`;

const Product = styled.div`
  display: flex;
  justify-content: space-between;
`;

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`;

const Image = styled.img`
  width: 180px;
  
`;
const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;
const ProductName = styled.span``;

const ProductID = styled.span``;
const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`;
const ProductSize = styled.span``;

const PriceDetails = styled.div`
  
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const ProductAmountContainer = styled.div`
  display: flex;

  align-items: center;
  margin-bottom: 20px;
`;
const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px;
`;
const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;
`;

const Hr = styled.hr`
    background-color: #000000;
    opacity: 50%;
    height: 1;
`;
const Summary = styled.div`
  flex: 1;
  border: 0.5px solid black;
  border-radius: 10px;
  padding: 17px;
  height: 50vh;
`;

const SummaryTitle = styled.h1`
  
`;

const SummaryItem = styled.div`
  
`;
const SummaryItemText = styled.span`
  
`;
const SummaryItemPrice = styled.span`
  
`;

const Button = styled.button`
  
`;

const Cart = () => {
  return (
    <Container>
      <Navbar />
      <Notification />
      <Wrapper>
        <Title>CART</Title>
        <Top>
          <TopButton>Continue shoping</TopButton>
          <TopTexts>
            <TopText>Shopping bag(2)</TopText>
            <TopText>Your Wishlist(0)</TopText>
          </TopTexts>
          <TopButton type="filled">Checkout now</TopButton>
        </Top>
        <Bottom>
          <Info>
            <Product>
              <ProductDetail>
                <Image src="https://media.istockphoto.com/photos/red-tshirt-clipping-path-picture-id465485445?k=20&m=465485445&s=612x612&w=0&h=DyxRzfbIlXX_jkdiokeAJ4takk3q5VjX1VOkiUgrxy0=" />
                <Details>
                  <ProductName>
                    <b>Product:</b> Red paint T shirt
                  </ProductName>
                  <ProductID>
                    <b>Id:</b> 9893937
                  </ProductID>
                  <ProductColor color="red" />
                  <ProductSize>
                    <b>Size:</b> XL
                  </ProductSize>
                </Details>
              </ProductDetail>
              <PriceDetails>
                <ProductAmountContainer>
                  <Add />
                  <ProductAmount>2</ProductAmount>
                  <Remove />
                </ProductAmountContainer>
                <ProductPrice>RS 1,300</ProductPrice>
              </PriceDetails>
            </Product>
            <Hr />

            <Product>
              <ProductDetail>
                <Image src="https://media.istockphoto.com/photos/running-shoes-picture-id1249496770?b=1&k=20&m=1249496770&s=170667a&w=0&h=_SUv4odBqZIzcXvdK9rqhPBIenbyBspPFiQOSDRi-RI=" />
                <Details>
                  <ProductName>
                    <b>Product:</b> Blue Shoes
                  </ProductName>
                  <ProductID>
                    <b>Id:</b> 9898837
                  </ProductID>
                  <ProductColor color="blue" />
                  <ProductSize>
                    <b>Size:</b> 37.5
                  </ProductSize>
                </Details>
              </ProductDetail>
              <PriceDetails>
                <ProductAmountContainer>
                  <Add />
                  <ProductAmount>2</ProductAmount>
                  <Remove />
                </ProductAmountContainer>
                <ProductPrice>RS 5,700</ProductPrice>
              </PriceDetails>
            </Product>
          </Info>
          <Summary>
            <SummaryTitle>
                Order Summary
            </SummaryTitle>
            <SummaryItem>
                <SummaryItemText>Sub Total</SummaryItemText>
                <SummaryItemPrice>Rs 7,000</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
                <SummaryItemText>Estimated Shipping</SummaryItemText>
                <SummaryItemPrice>Rs 800</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
                <SummaryItemText>Shiping Discount</SummaryItemText>
                <SummaryItemPrice>Rs -800</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
                <SummaryItemText type="total">Sub Total</SummaryItemText>
                <SummaryItemPrice>Rs 7,000</SummaryItemPrice>
            </SummaryItem>
            <Button> Chech out now</Button>
          </Summary>
        </Bottom>
      </Wrapper>
      <Fotter />
    </Container>
  );
};

export default Cart;
