import axios from 'axios'

const BASE_URL = "http://localhost:5000/api/"
const TOKEN ="eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyZDI5NTdlYTk1MTExMTFlZmJiODk3MyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1ODE0MzIxNywiZXhwIjoxNjU4NDAyNDE3fQ.4umTaHHoNBBDPIRkNW664sT6-Bj9bDlzyd7aRQuCNBU"

export const publicRequest = axios.create({
    baseURL:BASE_URL
})

export const userRequest = axios.create({
    header:{token:`Bearer ${TOKEN} `}
})
