import React from "react";
import styled from "styled-components";
import Fotter from "../components/Fotter";
import Navbar from "../components/Navbar";
import Newsletter from "../components/Newsletter";
import { Notification } from "../components/Notification";

const Container = styled.div``;

const Wrapper = styled.div`
  display: flex;
  padding: 50px;
`;

const ImageContainer = styled.div`
  flex: 1;
  padding: 0px 50 px;
`;

const Image = styled.img`
  width: 80%;
  height: 70vh;
  object-fit: fill;
`;

const InfoContainer = styled.div`
  flex: 1;
`;

const Title = styled.h1`
  font-weight: 500;
`;

const Desc = styled.p`
  margin: 10px 0px;
`;

const Price = styled.div`
  font-size: 40px;
  font-weight: 200;
`;

const FilterContainer = styled.div`
    
`
const Filter = styled.div`
    
`
const FilterTitle = styled.span`
    
`
const FilterColor = styled.div`
    
`
const FilterSize = styled.select`
    
`
const FilterSizeOption = styled.option`
    
`


const Product = () => {
  return (
    <Container>
      <Navbar />
      <Notification />
      <Wrapper>
        <ImageContainer>
          <Image src="https://i.pinimg.com/474x/20/45/89/20458914e7e373c2cecb72e6760dadda.jpg" />
        </ImageContainer>
        <InfoContainer>
          <Title>White T-Shirt</Title>
          <Desc>
            One of few designs with a front and back logo. This popular shark
            crunch over Muscle beach is perfect for any occasion. Custom Muscle
            Beach neck tag Super soft Spectra Spun ring-spun 100% cotton
            perfection fabric Unisex 100% NAFTA compliant
          </Desc>
          <Price> RS.1,500</Price>
          <FilterContainer>
            <Filter>
              <FilterTitle>Color</FilterTitle>
              <FilterColor color="black" />
              <FilterColor color="darkblue" />
              <FilterColor color="gray" />
            </Filter>
            <Filter>
              <FilterTitle>Size</FilterTitle>
              <FilterSize>
                <FilterSizeOption>XS</FilterSizeOption>
                <FilterSizeOption>S</FilterSizeOption>
                <FilterSizeOption>M</FilterSizeOption>
                <FilterSizeOption>L</FilterSizeOption>
                <FilterSizeOption>XL</FilterSizeOption>
              </FilterSize>
            </Filter>
          </FilterContainer>
        </InfoContainer>
      </Wrapper>

      <Newsletter />
      <Fotter />
    </Container>
  );
};

export default Product;
